    def openFile(self):
        filename = QtWidgets.QFileDialog.getOpenFileName(None, 'Open File', '',
                                                         'Image files (*.png *.xpm *.jpg *.tif)')
        if filename:
            self.image = cv2.imread(filename[0])
            self.showImage(self.lbl_input_image, self.image)
            print(filename)
        else:
            print("invalid file")

    def showImage(self, label: QtWidgets.QLabel, cv_img):
        if cv_img is None:
            cv_img = self.image

        height, width, channel = cv_img.shape
        bytes_per_line = 3 * width
        q_img = QtGui.QImage(cv_img.data, width, height, bytes_per_line, QtGui.QImage.Format_RGB888).rgbSwapped()
        label.setPixmap(QtGui.QPixmap(q_img))

    def denoise(self):
        # if self.image is not None:
        noise = np.zeros(self.image.shape, dtype=np.uint8)
        cv2.randn(noise, 0, 150)
        new_img = self.image + noise
        self.showImage(self.lbl_input_image, new_img)

    def createKernel(self):
        self.kernel_3x3 = np.ones((3, 3), np.float32) / 9
        self.kernel_5x5 = np.ones((5, 5), np.float32) / 25
        self.kernel_7x7 = np.ones((7, 7), np.float32) / 49
        if self.radioButton.isChecked():
            # print(kernel_3x3)
            return self.kernel_3x3
        if self.radioButton_2.isChecked():
            # print((kernel_7x7))
            return self.kernel_7x7
        if self.radioButton_3.isChecked():
            # print(kernel_5x5)
            return self.kernel_5x5

    def out_put(self):
        self.result_kernel = self.createKernel()
        # print(self.result_kernel)
        if self.image is not None:
            img = cv2.filter2D(self.image, -1, self.result_kernel)
            self.showImage(self.lbl_output_image, img)


